import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Login.css';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');  // Clear previous errors
    setLoading(true);  // Set loading before making the request

    try {
      // Make login request to backend
      const response = await axios.post('http://192.168.2.202:5000/login', { email, password });
      const token = response.data.token;

      // Check if token is received
      if (token) {
        // Store token in local storage
        localStorage.setItem('token', token);

        // Navigate to home or dashboard
        navigate('/home');
      } else {
        throw new Error('No token received from server');
      }
    } catch (err) {
      // Display appropriate error message
      setError(err.response?.data?.message || 'An error occurred during login. Please try again.');
    } finally {
      setLoading(false);  // Stop loading after request is completed
    }
  };

  return (
    <div className="login-container">
      <h2>Login</h2>

      {/* Error message */}
      {error && <p className="error-message">{error}</p>}

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            disabled={loading} // Disable input when loading
          />
        </div>

        <div className="form-group">
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            disabled={loading} // Disable input when loading
          />
        </div>

        {/* Display loading text when submitting */}
        <button type="submit" disabled={loading}>
          {loading ? 'Logging in...' : 'Login'}
        </button>
      </form>
    </div>
  );
};

export default Login;
