import React, { useState, useEffect } from 'react';
import Navbar from './Navbar';
import './Agents.css';
import { supabase } from './supabaseClient';  // Assuming you've initialized Supabase here

// Main Agents Component
const Agents = () => {
    const [comments, setComments] = useState([]);
    const [author, setAuthor] = useState('');
    const [text, setText] = useState('');

    // Fetch comments and their replies from Supabase when the component mounts
    useEffect(() => {
        const fetchComments = async () => {
            const { data, error } = await supabase
                .from('comments')
                .select(`
                    id, name, content, created_at,
                    replies (id, name, reply_content, created_at)
                `);
            if (error) {
                console.error("Error fetching comments:", error);
            } else {
                setComments(data);
            }
        };

        fetchComments();
    }, []);

    const handleCommentSubmit = async (e) => {
        e.preventDefault();
        // Insert a new comment into Supabase
        const { data, error } = await supabase
            .from('comments')
            .insert([
                { name: author, content: text }
            ]);
        if (error) {
            console.error("Error adding comment:", error);
        } else {
            setComments([...comments, { ...data[0], replies: [] }]);
            setAuthor('');
            setText('');
        }
    };

    const handleReplySubmit = async (commentId, replyText) => {
        const { data, error } = await supabase
            .from('replies')
            .insert([
                { comment_id: commentId, name: author, reply_content: replyText }
            ]);

        if (error) {
            console.error("Error adding reply:", error);
        } else {
            const updatedComments = comments.map((comment) => {
                if (comment.id === commentId) {
                    return { ...comment, replies: [...comment.replies, data[0]] };
                }
                return comment;
            });
            setComments(updatedComments);
        }
    };

    // Reply Component
    const Reply = ({ reply }) => (
        <div className="reply">
            <p><strong>{reply.name}:</strong> {reply.reply_content}</p>
            <p className="timestamp">{reply.created_at}</p>
        </div>
    );

    // Comment Component
    const Comment = ({ comment, onReply }) => {
        const [showReplyInput, setShowReplyInput] = useState(false);
        const [replyText, setReplyText] = useState('');

        const handleReplySubmit = (e) => {
            e.preventDefault();
            onReply(comment.id, replyText);
            setReplyText('');
            setShowReplyInput(false);
        };

        return (
            <div className="comment">
                <p><strong>{comment.name}:</strong> {comment.content}</p>
                <p className="timestamp">{comment.created_at}</p>
                <button onClick={() => setShowReplyInput(!showReplyInput)}>
                    {showReplyInput ? 'Cancel' : 'Reply'}
                </button>
                {showReplyInput && (
                    <form onSubmit={handleReplySubmit}>
                        <textarea
                            placeholder="Add a reply..."
                            value={replyText}
                            onChange={(e) => setReplyText(e.target.value)}
                            required
                        />
                        <button type="submit">Submit Reply</button>
                    </form>
                )}
                <div className="replies-list">
                    {comment.replies.map((reply, index) => (
                        <Reply key={index} reply={reply} />
                    ))}
                </div>
            </div>
        );
    };

    return (
        <div className="agents-section">
            <Navbar />
            <h1>Agents Page</h1>
            <h2>Comments</h2>
            <form onSubmit={handleCommentSubmit}>
                <input
                    type="text"
                    placeholder="Your Name"
                    value={author}
                    onChange={(e) => setAuthor(e.target.value)}
                    required
                />
                <textarea
                    placeholder="Add a comment..."
                    value={text}
                    onChange={(e) => setText(e.target.value)}
                    required
                />
                <button type="submit">Submit</button>
            </form>
            <div className="comments-list">
                {comments.map((comment) => (
                    <Comment key={comment.id} comment={comment} onReply={handleReplySubmit} />
                ))}
            </div>
        </div>
    );
};

export default Agents;
